import React, { useState, useEffect } from 'react';
import { TextField, Button, FormControl, Grid, Container, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';

const JefeView = () => {
  const [inventario, setInventario] = useState([]);
  const [formData, setFormData] = useState({
    noTela: '',
    talla: '',
    color: '',
    estilo: '',
    estado: ''
  });

  // Cargar los datos del inventario cuando se monta el componente
  useEffect(() => {
    fetchInventario();
  }, []);

  const fetchInventario = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/inventario');
      setInventario(response.data);
    } catch (error) {
      console.error('Error al obtener los inventarios:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:5000/api/inventario', formData);
      setFormData({ noTela: '', talla: '', color: '', estilo: '', estado: '' }); // Limpiar formulario
      fetchInventario(); // Actualizar los datos de la tabla
    } catch (error) {
      console.error('Error al guardar el inventario:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/inventario/${id}`);
      fetchInventario(); // Actualizar los datos de la tabla
    } catch (error) {
      console.error('Error al eliminar el inventario:', error);
    }
  };

  return (
    <Container maxWidth="md">
      <Typography variant="h4" align="center" gutterBottom>
        Vista del Jefe
      </Typography>

      <FormControl fullWidth margin="normal">
        <Grid container spacing={2} justifyContent="center">
          <Grid item xs={12} sm={6}>
            <TextField
              label="No. de Tela"
              variant="outlined"
              fullWidth
              required
              name="noTela"
              value={formData.noTela}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Talla"
              variant="outlined"
              fullWidth
              required
              name="talla"
              value={formData.talla}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Color"
              variant="outlined"
              fullWidth
              required
              name="color"
              value={formData.color}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Estilo"
              variant="outlined"
              fullWidth
              required
              name="estilo"
              value={formData.estilo}
              onChange={handleChange}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              label="Estado"
              variant="outlined"
              fullWidth
              required
              name="estado"
              value={formData.estado}
              onChange={handleChange}
            />
          </Grid>
        </Grid>

        <Grid container spacing={2} justifyContent="center" marginTop={2}>
          <Grid item>
            <Button variant="contained" color="primary" onClick={handleSubmit}>
              Guardar
            </Button>
          </Grid>
          <Grid item>
            <Button variant="outlined" color="secondary">
              Imprimir Reportes
            </Button>
          </Grid>
        </Grid>
      </FormControl>

      <TableContainer component={Paper} style={{ marginTop: '20px' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>No. de Tela</TableCell>
              <TableCell>Talla</TableCell>
              <TableCell>Color</TableCell>
              <TableCell>Estilo</TableCell>
              <TableCell>Estado</TableCell>
              <TableCell>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {inventario.map((item) => (
              <TableRow key={item._id}>
                <TableCell>{item.NoTela}</TableCell>
                <TableCell>{item.Talla}</TableCell>
                <TableCell>{item.Color}</TableCell>
                <TableCell>{item.Estilo}</TableCell>
                <TableCell>{item.Estado}</TableCell>
                <TableCell>
                  <IconButton color="secondary" onClick={() => handleDelete(item._id)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default JefeView;
