import React, { useState, useEffect } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { FormControl, Table, TableHead, TableRow, TableCell, TableBody, Paper } from '@mui/material';
import LogoutButton from './LogoutButton'; // Importar el botón de cerrar sesión
import axios from 'axios'; // Para hacer las solicitudes HTTP

const JefeView = () => {
  const [inventarios, setInventarios] = useState([]);
  const [formData, setFormData] = useState({
    NoTela: '',
    Talla: '',
    Color: '',
    Estilo: '',
    Estado: ''
  });

  // Cargar los inventarios desde el backend cuando el componente se monta
  useEffect(() => {
    fetchInventarios();
  }, []);

  // Función para cargar los inventarios desde la base de datos
  const fetchInventarios = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/inventario');
      setInventarios(response.data);
    } catch (error) {
      console.error('Error al cargar inventarios:', error);
    }
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/inventario', formData);
      setFormData({ NoTela: '', Talla: '', Color: '', Estilo: '', Estado: '' });
      fetchInventarios(); // Recargar los inventarios después de agregar uno nuevo
      alert('Inventario guardado exitosamente');
    } catch (error) {
      console.error('Error al guardar inventario:', error);
    }
  };

  // Función para manejar los cambios en el formulario
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  // Función para eliminar un inventario
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/inventario/${id}`);
      fetchInventarios(); // Recargar los inventarios después de eliminar uno
      alert('Inventario eliminado exitosamente');
    } catch (error) {
      console.error('Error al eliminar inventario:', error);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Vista del Jefe</h1>

      {/* Formulario para agregar inventario */}
      <FormControl style={{ margin: '20px auto', width: '300px' }}>
        <TextField 
          type="text" 
          label="No. Tela" 
          name="NoTela" 
          variant="outlined" 
          value={formData.NoTela} 
          onChange={handleInputChange} 
          margin="normal" 
        />
        <TextField 
          type="text" 
          label="Talla" 
          name="Talla" 
          variant="outlined" 
          value={formData.Talla} 
          onChange={handleInputChange} 
          margin="normal" 
        />
        <TextField 
          type="text" 
          label="Color" 
          name="Color" 
          variant="outlined" 
          value={formData.Color} 
          onChange={handleInputChange} 
          margin="normal" 
        />
        <TextField 
          type="text" 
          label="Estilo" 
          name="Estilo" 
          variant="outlined" 
          value={formData.Estilo} 
          onChange={handleInputChange} 
          margin="normal" 
        />
        <TextField 
          type="text" 
          label="Estado" 
          name="Estado" 
          variant="outlined" 
          value={formData.Estado} 
          onChange={handleInputChange} 
          margin="normal" 
        />
        <Button 
          variant="contained" 
          color="primary" 
          style={{ marginTop: '20px' }} 
          onClick={handleSubmit}
        >
          Guardar
        </Button>
      </FormControl>

      {/* Tabla para mostrar los inventarios */}
      <Paper style={{ marginTop: '40px', padding: '20px' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>No. Tela</TableCell>
              <TableCell>Talla</TableCell>
              <TableCell>Color</TableCell>
              <TableCell>Estilo</TableCell>
              <TableCell>Estado</TableCell>
              <TableCell>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {inventarios.map((inventario) => (
              <TableRow key={inventario._id}>
                <TableCell>{inventario.NoTela}</TableCell>
                <TableCell>{inventario.Talla}</TableCell>
                <TableCell>{inventario.Color}</TableCell>
                <TableCell>{inventario.Estilo}</TableCell>
                <TableCell>{inventario.Estado}</TableCell>
                <TableCell>
                  <Button 
                    variant="outlined" 
                    color="secondary" 
                    onClick={() => handleDelete(inventario._id)}
                  >
                    Eliminar
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>

      {/* Botón de cerrar sesión */}
      <LogoutButton />
    </div>
  );
};

export default JefeView;
